mapper.select:
select ec.id, ec.title, ec.cover, CONVERT(ec.price, DECIMAL(8,2)) AS price, ec.lesson_num AS lessonNum, ec.buy_count AS buyCount, ec.view_count AS viewCount, ecd.description,
t.id AS teacherId, t.name AS teacherName, t.intro, t.avatar,
s1.id AS subjectLevelOneId, s1.title AS subjectLevelOne, s2.id AS subjectLevelTwoId, s2.title AS subjectLevelTwo from edu_course ec left join edu_course_description ecd on ec.id=ecd.id left join edu_teacher t on ec.teacher_id=t.id left join edu_subject s1 on ec.subject_parent_id = s1.id left join edu_subject s2 on ec.subject_id=s2.id where ec.id=#{id}
